void initSegment(segment_t *seg, int idx, int turn, int inches, int speed)
{
    seg[idx].turn = turn;
    seg[idx].inches = inches;
    seg[idx].speed = speed;
}

void initializeRobot()
{
    tHTIRS2DSPMode mode = DSP_1200;
    HTIRS2setDSPMode(IRSeeker, mode);

    servoChangeRate[blockDump] = 5;

	servo[blockDump] = BLOCK_SERVO_RETRACTED;

    servo[middleElev] = SERVO_STOPPED;

    lightStripInit(0x1F);
    displayRestingPulse();

	/*
	 * Work around the RobotC bug
	 */
    initSegment(dead_reckon_from_b3, 0, 0, -12, 100);
    initSegment(dead_reckon_from_b3, 1, -90, -24, 100);
    initSegment(dead_reckon_from_b3, 2, -90, 32, 100);
    initSegment(dead_reckon_from_b3, 2, 0, 0, 0);

    initSegment(dead_reckon_from_b2, 0, 0, -23, 100);
    initSegment(dead_reckon_from_b2, 1, -90, -24, 100);
    initSegment(dead_reckon_from_b2, 2, -90, -32, 100);
    initSegment(dead_reckon_from_b2, 2, 0, 0, 0);

    initSegment(dead_reckon_from_b1, 0, 0, -45, 100);
    initSegment(dead_reckon_from_b1, 1, -90, -24, 100);
    initSegment(dead_reckon_from_b1, 2, -90, -32, 100);
    initSegment(dead_reckon_from_b1, 2, 0, 0, 0);

    initSegment(dead_reckon_from_b0, 0, 0, -55, 100);
    initSegment(dead_reckon_from_b0, 1, -90, -24, 100);
    initSegment(dead_reckon_from_b0, 2, -90, -32, 100);
    initSegment(dead_reckon_from_b0, 2, 0, 0, 0);
}

void raiseElevator()
{
	int val;

	nMotorEncoder[rightElevator] = 0;

	motor[leftElevator] = 70;
	motor[rightElevator] = -70;

    val = nMotorEncoder[rightElevator];
    while (val > ELEV_ENCODER_UP_VAL) {
        val = nMotorEncoder[rightElevator];
    }
	motor[leftElevator] = 0;
	motor[rightElevator] = 0;
}

void moveToRamp(int basket)
{
    switch (basket) {
    case 0:
        deadReckon(dead_reckon_from_b0);
        break;
    case 1:
        deadReckon(dead_reckon_from_b1);
        break;
    case 2:
        deadReckon(dead_reckon_from_b2);
       break;
    case 3:
        deadReckon(dead_reckon_from_b3);
        break;
    }
}

void dumpBlock()
{
    servoChangeRate[blockDump] = 10;
    servo[blockDump] = BLOCK_SERVO_EXTENDED;
    wait1Msec(1500);

    servoChangeRate[blockDump] = 10;

    for (int i = 0; i < 5; i++) {
        servo[blockDump] = BLOCK_SERVO_EXTENDED - 20;
        wait1Msec(100);
        servo[blockDump] = BLOCK_SERVO_EXTENDED;
    }

    servoChangeRate[blockDump] = 5;
    servo[blockDump] = BLOCK_SERVO_RETRACTED;
}

void showIRSegment(int dir)
{
    switch (dir) {

    case 5:
       flashGreen();
       break;
    default:
       displayCaution();
       break;
    }
}

int findIRBeacon()
{
    int dir;
    int i;

    for (i = 0; i < 4; i++) {
	    moveBackward(travelDistance[i], 50);
        wait1Msec(500);
        dir = HTIRS2readACDir(IRSeeker);
        showIRSegment(dir);
        nxtDisplayTextLine(2, "Reading %d", dir);

	    if (dir == 5) {
	        return i;
	    }
    }

    return -1;
}

