int findIRBeacon_v2()
{
    int acS1, acS2, acS3, acS4, acS5, _dirAC = 0;
    int max = 0;
    bool done;
    int dir;

    tHTIRS2DSPMode _mode = DSP_1200;
    HTIRS2setDSPMode(IRSeeker, _mode);

    moveBackwardOn(50);

    while (!done) {
	    HTIRS2readAllACStrength(IRSeeker, acS1, acS2, acS3, acS4, acS5);
	    dir = HTIRS2readACDir(IRSeeker);
	    if (dir == 5) {
            rangeFind();
        }
    }

    return 0;
}


